import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { filter, from, map, Observable } from 'rxjs';
import { AuthService } from '../services/auth.service';
import { ForecastService, WeatherForecast } from '../services/forecast.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  isAuthenticated$: Observable<boolean>;
  user$: Observable<any>;
  forecasts$: Observable<WeatherForecast[]> = from([]);
  externalForecasts$: Observable<WeatherForecast[]> = from([]);
  user: string | null = null;
  constructor(
    private authService: AuthService,
    private forecastService: ForecastService
  ) {
    this.isAuthenticated$ = from(this.authService.isAuthenticated());
    this.user$ = this.authService.getUser();
    this.externalForecasts$ = this.forecastService.fetchExternal();
  }

  getAccessTokenObj$(): Observable<string> {
    return this.user$.pipe(map((t) => JSON.parse(t)));
  }

 parseJwt (): Observable<object> {
    var base64Url = token.split('.')[1];
    var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    var jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload);
}

  ngOnInit() {}

  fetch() {
    this.forecasts$ = this.forecastService.fetch();
  }

  login() {
    this.authService.login();
  }

  logout() {
    this.authService.logout();
  }
}
<div>Welcome to home Route</div>

<div *ngIf="isAuthenticated$ | async; else noAuth">
  <button (click)="logout()">Logout</button>
  <button (click)="fetch()">Fetch forecasts</button>
  <hr />

  <br />

  Is Authenticated: {{ isAuthenticated$ | async }}
  <br />
  <div
    *ngIf="((externalForecasts$ | async) || []).length; else noExternalScope"
  >
    <br />
    External Forecasts
    <pre>{{ externalForecasts$ | async | json }}</pre>

    <br />
  </div>
  <div *ngIf="((forecasts$ | async) || []).length">
    <br />
    Forecasts
    <pre>{{ forecasts$ | async | json }}</pre>

    <br />
  </div>
  <br />
  userData
  <pre>{{ user$ | async | json }}</pre>

  <br />
</div>

<ng-template #noExternalScope>
  external forecast on behalf of the user permissions wasn't granted
  <hr />
</ng-template>

<ng-template #noAuth>
  <button (click)="login()">Login</button>
  <hr />
</ng-template>

