import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, Inject, OnInit } from '@angular/core';
import { filter, from, map, Observable } from 'rxjs';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  isAuthenticated$: Observable<boolean>;
  user$: Observable<any>;
  access_token: string = "";
  forecasts$: Observable<WeatherForecast[]> = from([]);
  user: string | null = null;
  constructor(
    private authService: AuthService,
    private http: HttpClient,
    @Inject('BASE_URL') private baseUrl: string) {
    this.isAuthenticated$ = from(this.authService.isAuthenticated());
    this.user$ = this.authService.getUser();
    this.user$.subscribe(u=> this.access_token = u.access_token);
  }

  getAccessTokenObj$(): Observable<string> {
    return this.user$.pipe(map(t=> JSON.parse(t) ));
  }


  ngOnInit() {
  }

  fetch() {
    var headers: HttpHeaders = new HttpHeaders();
    console.log(this.access_token);
    headers.append("Authentication", "Bearer "+this.access_token);
    var options = { headers };
    this.forecasts$= this.http.get<WeatherForecast[]>(this.baseUrl + 'weatherforecast', options);
  }

  login() {
    this.authService.login();
  }

  logout() {
    this.authService.logout();
  }
}

interface WeatherForecast {
  date: string;
  temperatureC: number;
  temperatureF: number;
  summary: string;
}

<div>Welcome to home Route</div>

<div *ngIf="isAuthenticated$|async; else noAuth">
  <button (click)="logout()">Logout</button>
  <button (click)="fetch()">Fetch forecasts</button>
  <hr />

  <br />

  Is Authenticated: {{ isAuthenticated$|async }}
  <br />
  <pre>{{user}}</pre>
  <br />
  Forecasts
   <pre>{{ forecasts$|async | json }}</pre>

  <br />

<pre>{{user}}</pre>
  <br />
  userData
   <pre>{{ user$|async | json }}</pre>

  <br />
</div>

<ng-template #noAuth>
  <button (click)="login()">Login</button>
  <hr />
</ng-template>

