import { Component, OnInit } from '@angular/core';
import { filter, from, map, Observable } from 'rxjs';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  isAuthenticated$: Observable<boolean>;
  user$: Observable<any>;
  user: string | null = null;
  constructor(private _authService: AuthService) {
    this.isAuthenticated$ = from(this._authService.isAuthenticated());
    this.user$ = this._authService.getUser();
  }

  getAccessTokenObj$(): Observable<string> {
    return this.user$.pipe(map(t=> JSON.parse(t) ));
  }


  ngOnInit() {
    var that = this;
    this._authService.callback(()=>{});
  }

  login() {
    this._authService.login();
  }

  refreshSession() {
  }

  logout() {
    this._authService.logout();
  }

  logoffAndRevokeTokens() {
  }

  revokeRefreshToken() {
  }

  revokeAccessToken() {
  }
}
<div>Welcome to home Route</div>

<div *ngIf="isAuthenticated$|async; else noAuth">
  <button (click)="logout()">Logout</button>
  <hr />

  <br />

  Is Authenticated: {{ isAuthenticated$|async }}
  <br />
<pre>{{user}}</pre>
  <br />
  userData
   <pre>{{ user$|async | json }}</pre>

  <br />
</div>

<ng-template #noAuth>
  <button (click)="login()">Login</button>
  <hr />
</ng-template>

